/*! Bootstrap Table Export Extension */
(function($){
  'use strict';
  var BootstrapTable = $.fn.bootstrapTable.Constructor,
      _initToolbar = BootstrapTable.prototype.initToolbar;
  BootstrapTable.prototype.initToolbar = function(){
    _initToolbar.apply(this, Array.prototype.slice.apply(arguments));
    if(this.options.showExport){
      var that = this;
      this.$toolbar.append('<button class="btn btn-default export" title="Exportar"><i class="fa fa-download"></i> Exportar</button>');
      this.$toolbar.find('button.export').off('click').on('click', function () {
        that.exportTable();
      });
    }
  };
  BootstrapTable.prototype.exportTable = function(){
    this.$el.tableExport({ type: 'excel', escape: false });
  };
})(jQuery);